mapper.insert:
        INSERT INTO POST 	(
                                POST_NO
                                , BBS_NO
                                , POST_TITLE
                                , WRT_ID
                                , WRT_DTT
								, TOP_POST_YN
								, DISP_YN
                                , VIEW_CNT
                                , REPLY_YN
                                , REG_NO
                                , REG_DTT
                                , MOD_NO
                                , MOD_DTT
                            )
        VALUES 				(
                                  #{postNo}
                                , #{bbsNo}
                                , #{postTitle}
                                , #{wrtId}
                                , NOW()
								, 'N'
								, #{dispYn}
                                , 0
                                , #{replyYn}
                                , #{regNo}
                                , NOW()
                                , #{modNo}
                                , NOW() 
                            )
        ON	DUPLICATE KEY 
        UPDATE
            POST_TITLE = #{postTitle}
            , REPLY_YN = #{replyYn}
            , DISP_YN = #{dispYn}
            , MOD_NO = #{modNo}
            , MOD_DTT = NOW()

INSERT INTO POST_CONTENTS (
                              BBS_NO
                            , POST_NO
                            , CONTENTS
                            )
VALUES 						(
                              #{bbsNo}
                            , #{postNo}
                            , #{contents}
                            )
ON	DUPLICATE KEY 
UPDATE
        CONTENTS = #{contents}

mapper.delete:
DELETE 
FROM    POST
WHERE   1 = 1
AND     POST_NO = #{postNo}
AND     BBS_NO = #{bbsNo}

DELETE 
FROM    POST_CONTENTS
WHERE   1 = 1
AND     POST_NO = #{postNo}
AND     BBS_NO = #{bbsNo}

mapper.insert:
INSERT INTO OBJ_LIKE (
                        LIKE_NO
                    , LOGIN_NO
                    , OBJ_CL_CD
                    , BBS_NO
                    , POST_NO
                    )
SELECT
      (SELECT IFNULL(MAX(LIKE_NO)+1, 1) FROM OBJ_LIKE)
    , #{loginNo}
    , '101100'
    , #{bbsNo}
    , #{postNo}

mapper.delete:
DELETE
FROM    OBJ_LIKE
WHERE   1 = 1
AND     LOGIN_NO = #{loginNo}
AND     OBJ_CL_CD = '101100'
AND     BBS_NO = #{bbsNo}
AND     POST_NO = #{postNo}

mapper.select:
SELECT
        IFNULL(MAX(REPLY_NO) + 1, 1) REPLY_NO
FROM    REPLY
WHERE   BBS_NO = #{bbsNo}
AND     POST_NO = #{postNo}

mapper.insert:
INSERT INTO REPLY   (
                        REPLY_NO
                      , BBS_NO
                      , POST_NO
                      , REPLY
                      , WRT_ID
                      , WRT_DTT
                      , REG_NO
                      , REG_DTT
                      , MOD_NO
                      , MOD_DTT
                    )
VALUES              (
                        #{replyNo}
                      , #{bbsNo}
                      , #{postNo}
                      , #{reply}
                      , #{wrtId}
                      , NOW()
                      , #{regNo}
                      , NOW()
                      , #{modNo}
                      , NOW()
                    )

mapper.delete:
DELETE
FROM    REPLY
WHERE   REPLY_NO = #{replyNo}
AND     BBS_NO = #{bbsNo}
AND     POST_NO = #{postNo}
